INSERT INTO `zbp_post` VALUES ('308', '15', '1', '{55}{38}{1}', '0', '0', 'web-kaifa-django-huanjing-dajian', '0', '0', 'web开发框架介绍（2）-Django开发环境搭建', '<p><span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑; line-height: 21px; background-color: rgb(255, 255, 255);\">本次我使用的环境是：</span></p>', '<div style=\"font-family: 微软雅黑; font-size: 14px; line-height: 21px; white-space: normal; widows: auto; background-color: #ffffff;\">本次我使用的环境是：</div><div style=\"font-family: 微软雅黑; font-size: 14px; line-height: 21px; white-space: normal; widows: auto; background-color: #ffffff;\"><ul style=\"margin-top: 0px; margin-bottom: 0px; background-color: inherit;\" class=\" list-paddingleft-2\"><li><p>python2.7.6</p></li><li><p>Django1.6.5</p></li><li><p>pycharm3.4</p></li><li><p>win7</p></li></ul><div style=\"background-color: inherit;\"></div></div><div style=\"font-family: 微软雅黑; font-size: 14px; line-height: 21px; white-space: normal; widows: auto; background-color: #ffffff;\">获取地址如下：</div><div style=\"font-family: 微软雅黑; font-size: 14px; line-height: 21px; white-space: normal; widows: auto; background-color: #ffffff;\">python：<a style=\"background-color: inherit; cursor: pointer; line-height: 1.5;\" href=\"https://www.python.org/downloads/\">https://www.python.org/downloads/</a></div><div style=\"font-family: 微软雅黑; font-size: 14px; line-height: 21px; white-space: normal; widows: auto; background-color: #ffffff;\">Django：<a style=\"background-color: inherit; cursor: pointer; line-height: 1.5;\" href=\"https://github.com/django/django\">https://github.com/django/django</a></div><div style=\"font-family: 微软雅黑; font-size: 14px; line-height: 21px; white-space: normal; widows: auto; background-color: #ffffff;\">pycharm：<a style=\"background-color: inherit; cursor: pointer; line-height: 1.5;\" href=\"https://www.jetbrains.com/pycharm/download/\">https://www.jetbrains.com/pycharm/download/</a></div><div style=\"font-family: 微软雅黑; font-size: 14px; line-height: 21px; white-space: normal; widows: auto; background-color: #ffffff;\">其中pycharm专业版需要收费，此处提供一个可用的注册码，亲测可用：</div><div style=\"font-family: 微软雅黑; font-size: 14px; line-height: 21px; white-space: normal; widows: auto; background-color: #ffffff;\">（下面的激活码来自互联网，仅供学习交流之用）</div><div style=\"font-family: 微软雅黑; font-size: 14px; line-height: 21px; white-space: normal; widows: auto; background-color: #ffffff;\">user name:</div><div style=\"font-family: 微软雅黑; font-size: 14px; line-height: 21px; white-space: normal; widows: auto; background-color: #ffffff;\">EMBRACE</div><div style=\"font-family: 微软雅黑; font-size: 14px; line-height: 21px; white-space: normal; widows: auto; background-color: #ffffff;\">key:</div><div style=\"font-family: 微软雅黑; font-size: 14px; line-height: 21px; white-space: normal; widows: auto; background-color: #ffffff;\">14203-12042010</div><div style=\"font-family: 微软雅黑; font-size: 14px; line-height: 21px; white-space: normal; widows: auto; background-color: #ffffff;\">0000107Iq75C621P7X1SFnpJDivKnX</div><div style=\"font-family: 微软雅黑; font-size: 14px; line-height: 21px; white-space: normal; widows: auto; background-color: #ffffff;\">6zcwYOYaGK3euO3ehd1MiTT&quot;2!Jny8</div><div style=\"font-family: 微软雅黑; font-size: 14px; line-height: 21px; white-space: normal; widows: auto; background-color: #ffffff;\">bff9VcTSJk7sRDLqKRVz1XGKbMqw3G</div><div style=\"font-family: 微软雅黑; font-size: 14px; line-height: 21px; white-space: normal; widows: auto; background-color: #ffffff;\"></div><div style=\"font-family: 微软雅黑; font-size: 14px; line-height: 21px; white-space: normal; widows: auto; background-color: #ffffff;\">Django开发环境搭建具体分为以下几个步骤，我会在下面列出我在各个步骤中遇到的问题：</div><div style=\"font-family: 微软雅黑; font-size: 14px; line-height: 21px; white-space: normal; widows: auto; background-color: #ffffff;\"><br style=\"background-color: inherit;\"/><table style=\"font-size: inherit; margin-top: 10px; margin-bottom: 10px; width: 688px; background-color: inherit;\" border=\"1\" cellspacing=\"0\" cellpadding=\"2\"><tbody style=\"background-color: inherit;\"><tr style=\"background-color: inherit;\" class=\"firstRow\"><td style=\"word-break: break-all; border: 1px solid #999999; padding: 5px 16px 5px 12px; min-height: 25px; min-width: 25px; height: 27px; background-color: inherit; width: 160px;\" valign=\"top\"><span style=\"font-size: inherit; line-height: 1.5;\">1.安装python</span></td><td style=\"word-break: break-all; border: 1px solid #999999; padding: 5px 16px 5px 12px; min-height: 25px; min-width: 25px; height: 27px; background-color: inherit; width: 486px;\" valign=\"top\">使用cmd测试安装python是否安装成功<br style=\"background-color: inherit;\"/><br style=\"background-color: inherit;\"/></td></tr><tr style=\"background-color: inherit;\"><td style=\"word-break: break-all; border: 1px solid #999999; padding: 5px 16px 5px 12px; min-height: 25px; min-width: 25px; height: 25px; background-color: inherit; width: 160px;\" valign=\"top\"><div style=\"min-width: 2px; background-color: inherit;\">2.安装Django</div></td><td style=\"word-break: break-all; border: 1px solid #999999; padding: 5px 16px 5px 12px; min-height: 25px; min-width: 25px; height: 25px; background-color: inherit; width: 486px;\" valign=\"top\"><div style=\"min-width: 2px; background-color: inherit;\">1、如果遇到问题：</div><div style=\"min-width: 2px; background-color: inherit;\">ImportError: No module named setuptools</div><div style=\"min-width: 2px; background-color: inherit;\">没有安装<span style=\"font-family: 微软雅黑; font-size: 14px;\">setuptools的package，需要先安装，解决办法如下：</span></div><div style=\"min-width: 2px; background-color: inherit;\"><a style=\"background-color: inherit; cursor: pointer;\" href=\"http://blog.sina.com.cn/s/blog_3fe961ae0100zgav.html\">http://blog.sina.com.cn/s/blog_3fe961ae0100zgav.html</a></div><div style=\"min-width: 2px; background-color: inherit;\"></div><div style=\"min-width: 2px; background-color: inherit;\">2、接着遇到如下问题：</div><div style=\"min-width: 2px; background-color: inherit;\">UnicodeDecodeError: &#39;ascii&#39; codec can&#39;t decode byte 0xb0</div><div style=\"min-width: 2px; background-color: inherit;\">原因是有些国内软件修改注册表导致python无法安装库，要改下lib目录下的代码，解决办法如下：</div><div style=\"min-width: 2px; background-color: inherit;\"><a style=\"background-color: inherit; cursor: pointer;\" href=\"http://blog.csdn.net/hugleecool/article/details/17996993\">http://blog.csdn.net/hugleecool/article/details/17996993</a></div><div style=\"min-width: 2px; background-color: inherit;\"></div><div style=\"min-width: 2px; background-color: inherit;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">3、最后遇到问题如下：</span></div><div style=\"min-width: 2px; background-color: inherit;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">Django安装WindowsError: [Error 2]</span></div><div style=\"min-width: 2px; background-color: inherit;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">原因是我之前安装过Django的版本，不需要重复安装，或者先卸载原先的旧版本即可。</span></div><div style=\"min-width: 2px; background-color: inherit;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">&nbsp;</span></div><div style=\"min-width: 2px; background-color: inherit;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">4、测试是否安装成功</span></div><div style=\"min-width: 2px; background-color: inherit;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">在cmd中进入python，运行如下命令：</span></div><div style=\"min-width: 2px; background-color: inherit;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">&nbsp; &nbsp; import django</span></div><div style=\"min-width: 2px; background-color: inherit;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">&nbsp; &nbsp; print django.VERSION</span></div><div style=\"min-width: 2px; background-color: inherit;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">&nbsp;</span></div></td></tr><tr style=\"background-color: inherit;\"><td style=\"word-break: break-all; border: 1px solid #999999; padding: 5px 16px 5px 12px; min-height: 25px; min-width: 25px; height: 25px; background-color: inherit; width: 160px;\" valign=\"top\"><div style=\"min-width: 2px; background-color: inherit;\">3。安装pycharm</div></td><td style=\"word-break: break-all; border: 1px solid #999999; padding: 5px 16px 5px 12px; min-height: 25px; min-width: 25px; height: 25px; background-color: inherit; width: 486px;\" valign=\"top\"><div style=\"min-width: 2px; background-color: inherit;\">1、激活码如上</div><div style=\"min-width: 2px; background-color: inherit;\"></div><div style=\"min-width: 2px; background-color: inherit;\">2、详见：</div><div style=\"min-width: 2px; background-color: inherit;\"><a style=\"background-color: inherit; cursor: pointer;\" href=\"http://my.oschina.net/hevakelcj/blog/384070\">http://my.oschina.net/hevakelcj/blog/384070</a></div><div style=\"min-width: 2px; background-color: inherit;\"><a style=\"background-color: inherit; cursor: pointer;\" href=\"http://my.oschina.net/u/914655/blog/517891\">http://my.oschina.net/u/914655/blog/517891</a></div><div style=\"min-width: 2px; background-color: inherit;\"></div></td></tr><tr style=\"background-color: inherit;\"><td style=\"word-break: break-all; border: 1px solid #999999; padding: 5px 16px 5px 12px; min-height: 25px; min-width: 25px; height: 27px; background-color: inherit; width: 160px;\" valign=\"top\"><div style=\"min-width: 2px; background-color: inherit;\"></div></td><td style=\"word-break: break-all; border: 1px solid #999999; padding: 5px 16px 5px 12px; min-height: 25px; min-width: 25px; height: 27px; background-color: inherit; width: 486px;\" valign=\"top\"><div style=\"min-width: 2px; background-color: inherit;\"></div></td></tr></tbody></table></div><div style=\"font-family: 微软雅黑; font-size: 14px; line-height: 21px; white-space: normal; widows: auto; background-color: #ffffff;\"></div><div style=\"font-family: 微软雅黑; font-size: 14px; line-height: 21px; white-space: normal; widows: auto; background-color: rgb(255, 255, 255);\"></div>', '1445850743', '0', '3', '', '');
INSERT INTO `zbp_post` VALUES ('309', '0', '1', '', '0', '1', '', '0', '0', '留言小本', '', '<p style=\"margin-top: 0px; margin-bottom: 28px; color: rgb(51, 51, 51); font-family: &#39;Noto Serif&#39;, serif; font-size: 17px; line-height: 28.0007px; white-space: normal;\">欢迎各位给我留言。</p><p style=\"margin-top: 0px; margin-bottom: 28px; color: rgb(51, 51, 51); font-family: &#39;Noto Serif&#39;, serif; font-size: 17px; line-height: 28.0007px; white-space: normal;\">也可以通过各种即时通讯工具联系我，我会尽快答复。</p><p><br/></p>', '1460453901', '0', '0', '', '');
